import { useDarkModeContext } from "../../context/DarkModeContext";
export const ItemCart = (item) => {
    const {darkMode} = useDarkModeContext();
    return (
        <div className="card mb-3 cardCart">
            <div className="row g-0">
                <div className="col-md-4">
                    <img src={`/img/${item.img}`} alt={`Imagen de producto ${item.nombre}`} className="img-fluid" />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h5 className="card-title">{item.nombre}{item.modelo}</h5>
                        <p className="card-text">Cantidad: {item.cantidad}</p>
                        <p className="card-text">Precio Unitario: ${new Intl.NumberFormat('de-DE').format(item.precio)}</p>
                        <p className="card-text">Subtotal: ${new Intl.NumberFormat('de-DE').format(item.precio * item.cantidad)}</p>
                        <button className={`btn ${darkMode ? 'btn-secondary' : 'btn-primary'} zoomIn d-block mx-auto`}></button>

                    </div>
                </div>
            </div>  
        </div>
    );
}
