import { useDarkModeContext } from "../../context/DarkModeContext";

export const Cart = () => {
    const { darkMode } = useDarkModeContext();
    const productos = [{nombre:"Arroz" , precio: 400, cantidad: 5},
                      {nombre:"Fideos", precio: 300, cantidad: 5},
                      {nombre:"Papas" , precio: 500, cantidad: 5}]

    const condLogica = true;
    return (
        <>
            {condLogica ?  <div className="container px-5">
                <div className="row align-items-center py-3">
                    <div className="col-12 text-center align-self-center mt-3">
                        <h2 className={`${darkMode ? 'text-light' : 'text-dark'} py-3`}>Carrito</h2>
                    </div>
                </div>
            </div> :  <div className="row align-items-center py-3">
                    <div className="col-12 text-center align-self-center mt-3">
                        <h2 className={`${darkMode ? 'text-light' : 'text-dark'} py-3`}>El Carrito está vacío</h2>
                    </div>
                </div>}


        </>
    )


}
